<script setup>
// 导航栏组件逻辑
// 侧边导航栏状态
import { ref } from 'vue';

const sideNavVisible = ref(false);

// 显示侧边导航栏方法
const showSideNav = () => {
    sideNavVisible.value = !sideNavVisible.value;

    // 触发自定义事件，通知父组件侧边导航栏状态变化
    emit('sideNavToggle', sideNavVisible.value);

    // 如果需要操作DOM元素，可以添加以下代码
    if (sideNavVisible.value) {
        document.body.classList.add('sidebar-open');
    } else {
        document.body.classList.remove('sidebar-open');
    }

    console.log('侧边导航栏状态:', sideNavVisible.value ? '显示' : '隐藏');
};

// 定义组件的emit事件

</script>

